import { useState } from 'react';

function Square({ value, onSquareClick }) {
    //every square receives a value X or O or Null 
    return (
	    <button className = "square" onClick = {onSquareClick}>
	    {value}
	</button> 
    ); 
}

export default function Board() {
    const [squares, setSquares] = useState(Array(9).fill(null));


    //creates a copy of squares arr nextSquares with slice() then updates each index to add
    //calling setsquares lets react know state of component has changed. 
    function handleClick() {
	const nextSquares = squares.slice();
	nextSquares[i] = "X"; 
	setSquares(nextSquares); 
    } 
  return (
    <> <div className="board-row">
	  //connect onSquareClick to function in Board by passing function
	  <Square value = {squares[0]} onSquareClick= {handleClick} />
	  // <Square value = {squares[0]} onSquareClick= {handleClick(0)} /> won't work because handleClick(0) alters the state of the board component by callign SetSquares 

        <Square value={squares[0]} />
        <Square value={squares[1]} />
        <Square value={squares[2]} />
      </div>
      <div className="board-row">
        <Square value={squares[3]} />
        <Square value={squares[4]} />
        <Square value={squares[5]} />
      </div>
      <div className="board-row">
        <Square value={squares[6]} />
        <Square value={squares[7]} />
        <Square value={squares[8]} />
      </div>
    </>
  );
}
