export default function Board() {
  // Function to render a single square
  function Square({ value }) {
      function handleClick() {
	  console.log('clicked!'); 
      }

      return (
	      <button className = "square" onClick={handleClick}
	      >
	      {value}
	  </button>
      );

  }

  // Function to render rows of squares
  const renderRow = (row) => {
    return (
      <div className="board-row">
        {Array.from({ length: 3 }, (_, i) => i + 1 + row * 3).map(value => (
          <Square key={value} value={value} />
        ))}
      </div>
    );
  }

  return (
    <>
      {Array.from({ length: 3 }, (_, i) => renderRow(i))}
    </>
  );
}
