import React, { useState } from 'react';

function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function isAdjacent(srcIndex, destIndex) {
  const srcRow = Math.floor(srcIndex / 3);
  const srcCol = srcIndex % 3;
  const destRow = Math.floor(destIndex / 3);
  const destCol = destIndex % 3;

  return Math.abs(srcRow - destRow) <= 1 && Math.abs(srcCol - destCol) <= 1;
}

function Board({ squares, onClick }) {
  const renderSquare = (i) => {
    return <Square value={squares[i]} onClick={() => onClick(i)} />;
  };

  return (
    <div>
      {[...Array(3)].map((_, row) => (
        <div key={row} className="board-row">
          {[...Array(3)].map((_, col) => renderSquare(row * 3 + col))}
        </div>
      ))}
    </div>
  );
}

function calculateWinner(squares) {
  // ... unchanged ...
}

function Game() {
  const [history, setHistory] = useState([{ squares: Array(9).fill(null), selectedSquare: -1 }]);
  const [stepNumber, setStepNumber] = useState(0);
  const xIsNext = (stepNumber % 2) === 0;

  function handleClick(i) {
    const historySlice = history.slice(0, stepNumber + 1);
    const current = historySlice[historySlice.length - 1];
    const squares = current.squares.slice();

    if (calculateWinner(squares) || squares[i]) {
      return;
    }

    if (stepNumber >= 6) {
      const selectedSquare = current.selectedSquare;
      if (selectedSquare >= 0 && squares[i] === null && isAdjacent(selectedSquare, i)) {
        squares[selectedSquare] = null;
        squares[i] = xIsNext ? 'X' : 'O';
        setHistory([...historySlice, { squares, selectedSquare: -1 }]);
        setStepNumber(historySlice.length);
      } else if (squares[i] === (xIsNext ? 'X' : 'O')) {
        setHistory(historySlice.map((frame, move) => move === stepNumber ? { ...frame, selectedSquare: i } : frame));
      }
    } else {
      squares[i] = xIsNext ? 'X' : 'O';
      setHistory([...historySlice, { squares, selectedSquare: -1 }]);
      setStepNumber(historySlice.length);
    }
  }

  function jumpTo(step) {
    setStepNumber(step);
    // Reset the selected square upon jumping to a previous step
    const resetHistory = history.map((frame, move) => move === step ? { ...frame, selectedSquare: -1 } : frame);
    setHistory(resetHistory);
  }

  const current = history[stepNumber];
  const winner = calculateWinner(current.squares);
  const status = winner ? `Winner: ${winner}` : `Next player: ${xIsNext ? 'X' : 'O'}`;

  return (
    <div className="game">
      <div className="game-board">
        <Board squares={current.squares} onClick={handleClick} />
      </div>
      <div className="game-info">
        <div>{status}</div>
        <ol>
          {history.map((_, move) => (
            <li key={move}>
              <button disabled={move === stepNumber} onClick={() => jumpTo(move)}>
                {move ? `Go to move #${move}` : 'Go to game start'}
              </button>
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default Game;
